import { useEffect, useState } from "react";
import axios from "../api/axios";

export default function Events() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const res = await axios.get("/events");
      setEvents(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to load events");
    } finally {
      setLoading(false);
    }
  };

  const bookEvent = async (eventId) => {
    try {
      await axios.post("/bookings", { eventId });
      alert("Event booked successfully ðŸŽ‰");
      fetchEvents(); // refresh seats
    } catch (err) {
      alert(err.response?.data?.message || "Booking failed");
    }
  };

  if (loading) return <p>Loading events...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Available Events</h2>

      {events.length === 0 && <p>No events found.</p>}

      {events.map((event) => (
        <div
          key={event._id}
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            marginBottom: "10px",
            borderRadius: "5px"
          }}
        >
          <h3>{event.title}</h3>
          <p><strong>Location:</strong> {event.location}</p>
          <p>
            <strong>Date:</strong>{" "}
            {new Date(event.date).toLocaleDateString()}
          </p>
          <p>
            <strong>Seats Left:</strong> {event.availableSeats}
          </p>

          <button
            disabled={event.availableSeats === 0}
            onClick={() => bookEvent(event._id)}
          >
            {event.availableSeats === 0 ? "Fully Booked" : "Book Event"}
          </button>
        </div>
      ))}
    </div>
  );
}
