import { useEffect, useState } from "react";
import axios from "../api/axios";

export default function Events() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
  const fetchEvents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/events");
      setEvents(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to load events");
    }
  };

  fetchEvents();
}, []);


  const bookEvent = async (eventId) => {
    try {
      await axios.post("/bookings", { eventId });
      alert("Event booked successfully ðŸŽ‰");
      fetchEvents(); // refresh seats
    } catch (err) {
      alert(err.response?.data?.message || "Booking failed");
    }
  };

  if (loading) return <p>Loading events...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1 className="page-title">Event Booking Website</h1>

<div className="events-grid">
  {events.map((event) => (
    <div className="event-card" key={event._id}>
      ...
    </div>
  ))}
</div>

      <h2>Available Events</h2>

      {events.length === 0 && <p>No events found.</p>}

      {events.map((event) => (
        <div
          key={event._id}
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            marginBottom: "10px",
            borderRadius: "5px"
          }}
        >
          <h3>{event.title}</h3>
          <p><strong>Location:</strong> {event.location}</p>
          <p>
            <strong>Date:</strong>{" "}
            {new Date(event.date).toLocaleDateString()}
          </p>
          <p className="price">
      {event.price === 0
        ? "Free Event"
        : event.price
          ? `â‚¹${event.price}`
          : "Price not set"}
    </p>

          <p>
  <strong>Seats Left:</strong>{" "}
  {event.availableSeats !== undefined ? event.availableSeats : "N/A"}
</p>


          <button
            disabled={event.availableSeats === 0}
            onClick={() => bookEvent(event._id)}
          >
            {event.availableSeats === 0 ? "Fully Booked" : "Book Event"}
          </button>
        </div>
      ))}
    </div>
  );
}
